import Item from './Item';
import { useState } from 'react'
import { Event } from '../models/event';

interface Props {
    events: Array<Event>;
    id: number;
    description: string;
    date: string;
    onDelete:(id: number) => void;
    onSearch:string;
}

const Events: React.FC<Props> = ({events, onDelete, onSearch}) => {

    const [hasError, setHasError] = useState(false);
    let itemsFound:number = 0;
    return (
        <>
        <h1>Event list: </h1>
        <ul>
        {events?.map((event, index) => {
            if (onSearch == "" || event.description.toLowerCase().includes(onSearch.toLowerCase())) {
                itemsFound++;
                return (
                    <Item 
                        key={event.id}
                        id={event.id}
                        description={event.description}
                        date={event.date} 
                        events={[]}
                        onDelete={onDelete}        
                    /> 
                )} else {
                    itemsFound - 1;
                }
                return null;
            })
        } 
        </ul>

        {itemsFound && <p>nothing found</p>}

    </>
    )}



export default Events
