import { useState } from 'react'

interface Props {
    onSort:string;
}

const Sort: React.FC<Props> = ({onSort}) => {

    const [sortBy, setSortBy] = useState('date');

    return (
    <div className="sort">
        <label htmlFor="sortEvents">Sort by</label>
        <select name="sortEvents" onChange={(e) => setSortBy(e.target.value)}>
            <option value="date">Date</option>
            <option value="creation">Created</option>
            <option value="description">Description</option>
        </select>
    </div>
    )

}

export default Sort
