import { useState } from 'react'
import Header from './components/Header';
import Events from './components/Events';
import AddEvent from './components/AddEvent';
import { Event } from './models/event';


interface Props {
  events?: Event;
  onDelete?:(id: number) => void;
  onAdd?:(description: string, date: string) => void;
}

const App: React.FC<Props> = ({events, onDelete}) => {

  const [list, setList] = useState<Event[]>([
    {
        id: 1,
        text: 'Example 1',
        date: '01/02/2023'    
    },
    {
        id: 2,
        text: 'Example 2',
        date: '01/03/2023'    
    },
    {
        id: 3,
        text: 'Example 3',
        date: '01/24/2023'    
    }
  ])

  // Delete event
  const deleteEvent = (id:number) => {
    setList(list.filter((event)=>event.id !== id))
  }

  // Add Event
  const addEvent = (description:string, date:string) => {
    console.log('hello from onAdd');
  }

  return (
    <div className="App">
      <Header />

      <AddEvent
        onAdd={addEvent}
      />

      {list.length> 0 ?<Events events={list} id={0} text="" date="" onDelete={deleteEvent}/>:'You have no events.'}
    </div>
  );
}

export default App;
