import { useState } from 'react'
import Header from './components/Header';
import Events from './components/Events';
import AddEvent from './components/AddEvent';
import { Event } from './models/event';


interface Props {
  events?: Event;
  onAdd?:(newEvent: object) => void;
  onDelete?:(id: number) => void;
}

const App: React.FC<Props> = ({events}) => {

  const [showAdd, setShowAdd] = useState(false);
  const [list, setList] = useState<Event[]>([
    {
        id: 1001,
        description: 'Example 1',
        date: '01/02/2023'    
    },
    {
        id: 2002,
        description: 'Example 2',
        date: '01/03/2023'    
    },
    {
        id: 3003,
        description: 'Example 3',
        date: '01/24/2023'    
    }
  ])

  // Delete event
  const deleteEvent = (id:number) => {
    setList(list.filter((event)=>event.id !== id))
  }

  // Add Event
  // why the hack did I have to create an object instead of just using newEvent.description
  const addEvent = (newEvent:object) => {
    const id = Math.floor(Math.random()*10000)+1;
    const eventObj = Object.create(newEvent);
    setList([...list,{id:id, description:eventObj.description, date:eventObj.date}]);
  }

  return (
    <div className="App">
      <Header onShow={() => setShowAdd(!showAdd)} />

      { showAdd && <AddEvent onAdd={addEvent} /> }

      {list.length> 0 ?<Events events={list} id={0} description="" date="" onDelete={deleteEvent}/>:'You have no events.'}
    </div>
  );
}

export default App;
