import { useEffect, useState } from 'react'
import Header from './components/Header';
import Events from './components/Events';
import AddEvent from './components/AddEvent';
import { Event } from './models/event';


interface Props {
  events?: Event;
  onAdd?:(newEvent: object) => void;
  onDelete?:(id: number) => void;
  onSort?:(sort: string) => void;
  onSearch?:(search: string) => void;
}

const App: React.FC<Props> = ({events}) => {

  const [showAdd, setShowAdd] = useState(false);
  const [searchInput, setSearchInput] = useState<string>('');
  const [fileredList, setFileredList] = useState<Event[]>([]);

  const [list, setList] = useState<Event[]>([
    {
        id: 1001,
        description: 'ZExample 100',
        date: '09/06/2015'    
    },
    {
        id: 2002,
        description: 'BExample 99',
        date: '09/06/2019'    
    },
    {
        id: 3003,
        description: 'Example 101',
        date: '09/06/2013'    
    }
  ])

  //const [filteredResults, setFilteredResults] = useState<Event[]>(list);

  // Delete event
  const deleteEvent = (id:number) => {
    setList(list.filter((event)=>event.id !== id))
  }

  // Add Event
  // why the hack did I have to create an object instead of just using newEvent.description
  const addEvent = (newEvent:object) => {
    const id = Math.floor(Math.random()*10000)+1;
    const eventObj = Object.create(newEvent);
    setList([...list,{id:id, description:eventObj.description, date:eventObj.date}]);
  }

  // sort events
  const sortEvents = (sort:string) => {
    if(sort=="date") {
      sortDate("date");
    } else if (sort=="created") {
      sortDate("date");
    } else {
      sortDesc();
    }
  }

  // sort by description/title
  const sortDesc = () => {
    const sorted = [...list].sort((a, b) => {
      const nameA = a.description.toUpperCase(); // ignore upper and lowercase
      const nameB = b.description.toUpperCase(); // ignore upper and lowercase
      if (nameA < nameB) {return -1;}
      if (nameA > nameB) {return 1;}
      return 0;
    }); 
    setList(sorted);
  }

  // sort by date
  const sortDate = (typeKey:string) => {
    const sorted = [...list].sort((a, b) => {
        const newA = (a[typeKey as keyof typeof a] as string).split('/').reverse().join('-');
        const newB = (b[typeKey as keyof typeof a] as string).split('/').reverse().join('-');
        type OnlyKeys = keyof typeof a;
        return +new Date(newA) - +new Date(newB)
      }
    );
    setList(sorted);
  };

  // search by description/title
  const searchItems = (searchValue:string) => {
    setSearchInput(searchValue)
    if (searchInput !== '') {
      const filteredData = list.filter((event) => {
          return Object.values(event.description).join('').toLowerCase().includes(searchInput.toLowerCase())
      })
      setFileredList(filteredData)
    } else {
      setFileredList([])
    }
  }

  // const eventList: Array<Event> = [];

  // if(fileredList.length>0) {
  //     const eventList: Array<Event> = fileredList;
  // } else {
  //     const eventList: Array<Event> = list;
  // }

  const eventList = () => {
    if(fileredList.length>0) {
      console.log(1);
      const eventList: Array<Event> = fileredList;
    } else {
      console.log(2);
      const eventList: Array<Event> = list;
    }
    return eventList
  }

  return (

    <div className="App">
      <Header onShow={() => setShowAdd(!showAdd)} statusAdd={showAdd} onSort={sortEvents} onSearch={searchItems} />
      { showAdd && <AddEvent onAdd={addEvent} /> }

      {list.length> 0 ?<Events events={list} id={0} description="" date="" onDelete={deleteEvent} />:'You have no events.'}

    </div>
  );
}

export default App;
